import React, {Component} from 'react'
import TodoDataService from '../../api/todo/TodoDataService.js'
import AuthenticationService from './AuthenticationService.js'

class ListTodoComponent extends Component{
    constructor(props) {
        super(props)
        console.log('constructor')
        this.state = {
            todos : []
        }
        // binding
        this.deleteTodoClicked = this.deleteTodoClicked.bind(this)
    }

    componentDidMount() {
        console.log('mount')
        // this method is called immediately after the component is mounted
        // here is the right place to load data from a remote endpoint
        let username = AuthenticationService.getLoggedInUser()
        TodoDataService.retrieveAllTodos(username)
            .then(
                // response => console.log(response)
                response => {
                    this.setState ({todos : response.data})
                }
            )
    }

    render() {
        console.log('render')
        return (
            <div>
                <h1>Todo List</h1>
                <div className="container">
                    <table className="table">
                        <thead>
                            <tr>
                                <th>id</th>
                                <th>Description</th>
                                <th>Target Date</th>
                                <th>Is Completed?</th>
                                <th>Operations</th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                this.state.todos.map (
                                    todo =>
                                        <tr key={todo.id}>
                                            <td>{todo.id}</td>
                                            <td>{todo.description}</td>
                                            <td>{todo.targetDate.toString()}</td>
                                            <td>{todo.done.toString()}</td>
                                            <td><button className="btn btn-warning" onClick={() => this.deleteTodoClicked(todo.id)}>delete</button></td>
                                        </tr>
                                        // all attributes' names must match their names in backend data
                                )
                            }
                        </tbody>
                    </table>
                </div>
            </div>
        )
    }

    deleteTodoClicked(id) {
        let username = TodoDataService.getLoggedInUser()
        // TodoDataService.deleteTodos(username, id)
        console.log(username + " " + id)
    }
}

export default ListTodoComponent
