import React, { Component } from "react";
import propTypes from "prop-types"
import './Counter.css'

class Counter extends Component {
    render() {
      return (
        <div className="App">
          <CounterButton by={1}/>
          <CounterButton by={5}/>
          <CounterButton by={10}/>
        </div>
      );
    }
  }

class CounterButton extends Component {
    constructor() {
        super()
        this.state = {
            counter : 0
        }
        this.increment = this.increment.bind(this)
        this.decrement = this.decrement.bind(this)
    }

    // we can change original function into arrow function
    // we can also include inline css in JSX
    render() {
        // const style = {fontSize: "35px", padding: "15px 30px"}
        return (
            <div className="counter">
            <button onClick={this.increment}>+{this.props.by}</button>
            <button onClick={this.decrement}>-{this.props.by}</button>
            <span className="count" 
            //  style={style}
            >{this.state.counter}
            </span>
            </div>
        )
    }

    increment() {
        //console.log("Increment");
        this.setState({
            counter : this.state.counter + this.props.by
        })
    }

    decrement() {
        this.setState({
            counter : this.state.counter - this.props.by
        })
    }
}

// set a default value for props in Counter
CounterButton.defaultProps = {
    by : 1
}
// Type check when passing value to props
CounterButton.propTypes = {
    by : propTypes.number
}



export default Counter
